import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("");
  function uppercaseFunc() {
    console.log("Uppercase button clicked");
    let newText = text.toUpperCase();
    setText(newText);
  }
  function changed(e) {
    console.log("textarea changed");
    setText(e.target.value);
  }
  return (
    <>
      <div className="container">
        <div className="mb-1 my-3">
          <h1>{props.heading}</h1>

          <textarea
            className="form-control fs-3"
            id="mybox"
            rows="7"
            value={text}
            onChange={changed}
          ></textarea>
          <button className="btn btn-primary my-2" onClick={uppercaseFunc}>
            Uppercase
          </button>
          <button className="btn btn-primary my-2 mx-2" onClick={uppercaseFunc}>
            Preview
          </button>
        </div>
      </div>
      <div className="container">
        <h1>Your text summary</h1>
        <p className="fs-5">
          {text.split(" ").length - 1} words {text.length} character
        </p>
        <p className="fs-5" >
        {0.008*(text.split(" ").length - 1)} minutes to above paragraph
        </p>
      </div>
    </>
  );
}
