import React, { useState } from "react";


export default function TextForm(props) {
  const [text, setText] = useState("");
  function uppercaseFunc() {
    console.log("Uppercase button clicked");
    let newText= text.toUpperCase();
    setText(newText);
  }
  function changed(e) {
    console.log("textarea changed");
    setText(e.target.value);
  }
  return (
    <>
    <div className="container">
      <div className="mb-3 my-4">
        <h1>{props.heading}</h1>

        <textarea className="form-control " id="mybox" rows="8" value={text} onChange={changed}></textarea>
        <button className="btn btn-primary my-2"  onClick={uppercaseFunc}>Uppercase</button>
      </div>
    </div>
    <div className="container">
      <h1>Your text summary</h1>
      <p className="fs-4">{text.split(" ").length-(1)} words {text.length} character</p>
    </div>
    </>
  );
}
